<?php	class Contact extends mysql{				var $contacts;		var $query;		var $result;				function __construct(){			parent::__construct();					}				function query($sql){			if( $sql !=''):					$this -> query = $sql;			endif;		}				function initContact(){			$res = $this -> execute($this -> query,__FILE__,__LINE__);			if(!$this -> eof($res) ) return true;			else return false;		}						function result(){			if( !empty($this->query)):				$this -> result = $this->execute($this->query,__FILE__,__LINE__);			endif;			}				function rows(){			if($this->result):				return $datas[] = $this->fetchrow($this -> result);			else:				return false;			endif;		}				function where($filter=array()){			$sql ='';			foreach($filter as $key => $value):				$sql.=" AND $key ='$value' ";			endforeach;						$this -> query.= $sql;		}					function optWhere($Query){			if($Query) $this->query .= $Query;			else $this->query;		}		}		if(!is_object($Contact)){		$Contact = new Contact();	}	?>