<?php	require("../sisipan/sessions.php");	require("../fungsi/global.php");	require("../class/MYSQLConnect.php");		class Updater extends mysql{		function __construct(){			parent::__construct();		}				function UpdateThemes(){								if($this->havepost('action')):				$param_value = $this -> escPost('themes_value');				$desc_value  = $this -> escPost('themes_name');								$datas = array('param_value' => $param_value, 'description'=>$desc_value);				$where = array('param_name' => 'THEME','module_name'=>'CONFIG');								$res = $this ->set_mysql_update('tms_application_config',$datas,$where);				if( $res ): echo 1;				else :					echo 0;				endif;			endif;		}	}		$Updater = new Updater();		if( $Updater ): $Updater -> UpdateThemes(); endif;?>