<?phprequire(dirname(__FILE__)."/../sisipan/sessions.php");require(dirname(__FILE__)."/../fungsi/global.php");require(dirname(__FILE__)."/../class/MYSQLConnect.php");	class UserReady extends mysql{	function UserReady()	{		parent::__construct();		self::index();				}			function index()	{		if( $this -> havepost('action') )		{			switch($_REQUEST['action'])			{				case 'user_state' : $this -> UserReadyState(); break;			}		}	}		function UserReadyState()	{		$sql = " SELECT 					c.id as username,					c.full_name as Fullname,					d.name as ext_status, 					e.name as agent_status,					CASE e.code						WHEN 0 then '#FF4321'						WHEN 1 then '#334607'						WHEN 2 then '#9fa6a8'						WHEN 3 then '#9fa6a8'						WHEN 4 then '#334607'					END as Color					FROM cc_agent a 					LEFT OUTER JOIN cc_agent_activity b ON a.id = b.agent					LEFT OUTER JOIN tms_agent c ON a.userid = c.id					LEFT JOIN cc_agent_extension_status d on b.ext_status=d.code					LEFT JOIN cc_agent_activity_code e on b.`status`=e.code					WHERE c.user_state='1'					AND b.`status` is not null ";		$qry = $this -> query($sql);					echo "<table cellpadding=\"6px;\" cellspacing=\"0\" style=\"margin-left:3px;border-right:1px solid #dddddd;border-bottom:1px solid #dddddd;\" width=\"99%\" align=\"center\">				<tr>					<th style=\"background-color:#7fa1d9;text-align:left;color:#082b66;height:24px;font-family:Arial;font-size:12px;border-left:1px solid #dddddd;border-top:1px solid #dddddd;\"> User Name </th>					<th style=\"background-color:#7fa1d9;text-align:left;color:#082b66;height:24px;font-family:Arial;font-size:12px;border-left:1px solid #dddddd;border-top:1px solid #dddddd;\"> Agent Status </th>					<th style=\"background-color:#7fa1d9;text-align:left;color:#082b66;height:24px;font-family:Arial;font-size:12px;border-left:1px solid #dddddd;border-top:1px solid #dddddd;\"> Extension Status </th>				</tr>				";				foreach($qry-> result_assoc() as $rows ){			echo " <tr>						<td style=\"height:24px;font-family:Arial;font-size:12px;border-left:1px solid #dddddd;border-top:1px solid #dddddd;\">							<a href=\"javascript:void(0);\" id=\"".$rows['username']."\" onclick=\"javascript:ChatWithUser(this);\" style=\"text-decoration:none;color:$rows[Color];\" title=\"click here for chat...\">".$rows['Fullname']."</a></td>						<td style=\"height:24px;font-family:Arial;font-size:12px;border-left:1px solid #dddddd;border-top:1px solid #dddddd;\"><span style=\"color:$rows[Color];font-weight:bold;\">".$rows['agent_status']."</span></td>						<td style=\"height:24px;font-family:Arial;font-size:12px;border-left:1px solid #dddddd;border-top:1px solid #dddddd;\"><span style=\"color:$rows[Color];font-weight:bold;\">".$rows['ext_status']."</span></td>				   </tr> ";											}				echo "</table>";	}}new UserReady();?>