<?phpclass DBquery extends mysql{	private $SQL_string;		function DBquery($SQL_string = NULL)	{			$this -> SQL_string = $SQL_string;		parent::__construct();	}	/** * function factory records  * return array datas  */			function query() 	{		if( $this -> dbconn )		{			$qry_result = $this -> execute($this -> SQL_string,__FILE__,__LINE__);			if( !$qry_result ) exit(mysql_error());			else				return $qry_result;		}			else exit('ERROR :'.mysql_error());	}	/** * function factory records  * return array datas  */		 function result_assoc()	{		$qry =  $this -> query();		while( $rows = @mysql_fetch_assoc($qry)){			$datas[] = $rows; 		}		return $datas;	}	/** * function factory records  * return array datas  */		 function result_singgle_value()	{		$qry =  $this -> query();		$rows = @mysql_fetch_array($qry);				if( $rows[0] !='' )		{ 			return $rows[0];		}	}		/** * function factory records  * return array datas  */		 function result_first_assoc()	{		$qry =  $this -> query();		$rows = @mysql_fetch_assoc($qry);		if( is_array($rows) ) 		{			return $rows;		}		}	/** * function factory records  * return array datas  */		 function result_first_array()	{		$qry =  $this -> query();		$rows = @mysql_fetch_array($qry);		if( is_array($rows) ) 		{			return $rows;		}		}	/** * function factory records  * return array datas  */		 function result_first_rows()	{		$qry =  $this -> query();		$rows = @mysql_fetch_row($qry);		if( is_array($rows) ) 		{			return $rows;		}		}	/** * function factory records  * return array datas  */		 function result_first_object()	{		$qry =  $this -> query();		$rows = @mysql_fetch_object($qry);		if( is_array($rows) ) 		{			return $rows;		}		}		/** * function factory records  * return array datas  */	 function result_rows()	{		$qry =  $this -> query();		while( $rows = @mysql_fetch_row($qry)){			$datas[] = $rows; 		}				return $datas;	}		/** * function factory records  * return array datas  */		 function result_object()	{		$qry =  $this -> query();		while( $rows = @mysql_fetch_object($qry)){			$datas[] = $rows; 		}		return $datas;	}	/** * function factory records  * return array datas  */		function result_array()	{		$datas = array();		$result = $this -> query();		if( $result )		{			while( $rows = @mysql_fetch_array($result)){				$datas[] = $rows; 			}		}				return $datas;	}	/** * function factory records  * return array datas  */		 function result_field()	{		$datas = array();		$result = $this -> query();		if( $result )		{			while( $rows = @mysql_fetch_field($result)){				$datas[] = $rows; 			}		}		return $datas;	}		/** * function factory records  * return array datas  */	  function result_field_name()	{		$datas = array();		$result = $this -> result_field();		foreach( $result as $rows ){			$datas[] = $rows->name; 		}		return $datas;	}	/** * function factory records  * return array datas  */		 function result_field_table()	{		$datas  = array();		$result = $this -> result_field();		if( is_array($result))		{			return $result[0]->table;		}	}	/** * function factory records  * return array datas  */	 function result_num_rows()	{		$qry =  $this -> query();		if( $qry ){			return mysql_num_rows($qry);		}	}	/** get return $sql string **/function result_str_query()	{		if( !empty( $this -> SQL_string ) )		{			return $this -> SQL_string;		}		else			return NULL;	}/** get return value definition ***/function result_get_value($field='', $row=0)		{		$datas  = array();		foreach($this -> result_array() as $rows )		{				$datas[] = $rows;		}				if( !empty($field) ){			return $datas[$row][$field];		}			else{			return $datas[$row][0];		}		}	/*** function result_EOF ***/function EOF()	{		if( $this -> result_num_rows() > 0 ) 			return false; 		else			return true;	}	 	}	?>