<?php	require(dirname(__FILE__)."/../sisipan/sessions.php");	require(dirname(__FILE__)."/../class/MYSQLConnect.php");	require(dirname(__FILE__)."/../class/class.application.php");	require(dirname(__FILE__)."/../class/class.getfunction.php");	require(dirname(__FILE__)."/../sisipan/parameters.php");	require(dirname(__FILE__)."/../class/lib.form.php");		/* chek is cutomer was register before ***/	$CustomerDOB = NULL;		$sql = " SELECT COUNT(a.CustomerId) as jml, b.CustomerDOB from t_gn_policyautogen a  			 INNER join t_gn_customer b on a.CustomerId=b.CustomerId 			 WHERE  a.CustomerId='".$_REQUEST['customerid']."'";	$qry = $db -> query($sql);    if( $qry -> result_get_value('jml') > 0 )	{		$CustomerDOB = $db -> Date -> indonesia($qry -> result_get_value('CustomerDOB'),'-');	}		?><html xmlns:o="urn:schemas-microsoft-com:office:office" xmlns:x="urn:schemas-microsoft-com:office:excel" xmlns="http://www.w3.org/TR/REC-html40"><head><meta http-equiv=Content-Type content="text/html; charset=windows-1252"><meta name=ProgId content=Excel.Sheet><meta name=Generator content="Microsoft Excel 12"><link rel=File-List href="Book1_files/filelist.xml"><script type="text/javascript" src="<?php echo $app->basePath();?>pustaka/jquery/jquery-1.3.2.js"></script>    <script type="text/javascript" src="<?php echo $app->basePath();?>pustaka/jquery/jquery-ui-1.7.2/ui/jquery-ui.js"></script><script type="text/javascript" src="<?php echo $app->basePath();?>pustaka/jquery/jquery-ui-1.7.2/external/bgiframe/jquery.bgiframe.js"></script><script type="text/javascript" src="<?php echo $app->basePath();?>js/javaclass.js"></script><script>/////////////////////////////////////////////////////////////// ***************************************************** ////var CustomerId = '<?php echo $_REQUEST['customerid'];?>';var CampaignId = '<?php echo $_REQUEST['campaignid'];?>';var ProductId  = '<?php echo $getFunction -> getProductId();?>';	/////////////////////////////////////////////////////////////// ***************************************************** ////		var verifikasiDOB = function(opts)	{		var stringvalue   = opts.value		var input_value   = opts.value;		var images 		  = doJava.dom('validation');		var default_value;				if( input_value.length ==2 ){			default_value = stringvalue.substring(0,2);			opts.value = default_value;			doJava.dom('button_save').disabled=true;		}		else if( input_value.length==3 )		{			default_value = stringvalue.substring(0,2)+'-'+stringvalue.substring(2,4);			opts.value = default_value;			doJava.dom('button_save').disabled=true;		}		else if( input_value.length ==5 && input_value.length < 10 )		{			default_value = stringvalue.substring(0,2)+'-'+stringvalue.substring(3,6)+'-'+stringvalue.substring(5,8);			opts.value = default_value;			doJava.dom('button_save').disabled=true;		}		else if( input_value.length ==10){			doJava.File = '../class/class.verifikasi.dob.php';			doJava.Params = {				action:'verifikasi_dob',				customer_dob : opts.value,				CustomerId : CustomerId			}			var msg_error = doJava.eJson();			if( msg_error.result==1){				images.innerHTML = "&nbsp;&nbsp;<img src=../gambar/icon/accept.png>";				doJava.dom('button_save').disabled=false;				opts.disabled=true;			}			else{				images.innerHTML = "&nbsp;&nbsp;<img src=../gambar/icon/cancel.png>"; 				doJava.dom('button_save').disabled=true;				opts.disabled=false;			}		}	}/////////////////////////////////////////////////////////////// ***************************************************** ////		var SavePolicy= function()	{		var CustomerDob = doJava.dom('customer_dob').value;			doJava.File = '../class/class.save.creditshield.php';			doJava.Params = {				action:'save_create_policy',				CustomerId : CustomerId,				CampaignId : CampaignId,				CustomerDob : CustomerDob			}				var msg_error = doJava.eJson();		if( msg_error.result==1)		{			alert('Success, Save Policy with Closing Number ( '+msg_error.closingnumber+' ) ..!');			return true;		}		else if( msg_error.result==2 ){			alert('Warning, Closing Number ( '+msg_error.closingnumber+' ) alerdy exist..!');			return true;		}		else{			alert('Failed, Save Policy!');			return false;		}		}/////////////////////////////////////////////////////////////// ***************************************************** ////		var InfoProductBenefit = function()	{			if( ProductId!=''){			doJava.File  = '../class/class.frm.tpl.php';			doJava.Params = { action :'get_form_plan', productid : ProductId, planbenef	: '' }			doJava.winew.winconfig=			{				location: doJava.File+'?'+doJava.ArrVal(),				width: 900, height : 400,				left : parseInt((screen.availWidth/2) - (doJava.winew.winconfig.width)),				top : parseInt((screen.availHeight/2) - (doJava.winew.winconfig.height)),				windowName: 'windowName3', resizable:false, 				menubar	: false, scrollbars	: true, 				status: false, toolbar: false			};						doJava.winew.open();  		}	}		/////////////////////////////////////////////////////////////// ***************************************************** ////		new(function(){		$(function(){			doJava.dom('button_save').disabled=true;		});	});/////////////////////////////////////////////////////////////// ***************************************************** ////	var ExitPolicy = function()	{		doJava.winew.winClose(); 		return false;	}	</script><style>	div#pages{		width:auto;		border:1px solid #ddd;		margin: 2px 2px 2px 2px;		padding: 5px 5px 5px 5px;	}		table.form td{		font-family:Trebuchet MS;		padding-left:4px;		}		.header{		font-family:Trebuchet MS;		padding-left:4px;		font-size:12px;			color:#BBB000;		font-weight:bold;	}	.inputtext{		border:1px solid #FF4321;		height:23px;		background-color:#FFFCCC;		width:220px;	}</style></head><body>	<div id="pages">		<table border=0 cellpadding=0 cellspacing=0 width="99%" align="center" class="form">			<tr>				<td colspan=4 style='height:27px;background-color:#BBBDDD;font-weight:bold;border-bottom:1px solid blue;'>FORM PRODUCT CREDIT SHIELD</td>			</tr>			<tr>				<td colspan=4>&nbsp;</td>			</tr>			<tr height=20 style='height:15.0pt'>			  <td class="header" >Customer Name</td>			  <td class="content"><?php echo $jpForm-> jpField('customer_name','inputtext',$getFunction->CustData->CustomerFirstName,NULL,1);?></td>			  <td class="header">Verifikasi DOB</td>			  <td class="content"><?php echo $jpForm-> jpField('customer_dob','inputtext',$CustomerDOB,'onkeyup="verifikasiDOB(this);";',0,10);?><span style="color:red;font-size:11px;" id="validation"> ( * dd-mm-yyyy)</span></td>			</tr>						<tr>				<td colspan=4 style="border-bottom:1px solid #eee;">&nbsp;</td>			</tr>						<tr>				<td colspan=4 style="padding-top:5px;" align="right">					<input type="button" name="button_save" id="button_save" value="Save" onclick="SavePolicy();"> &nbsp;					<input type="button" name="button_exit" id="button_exit" value="Exit" onclick="ExitPolicy();"> &nbsp;						<input type="button" name="button_exit" id="button_exit" value="Info" onclick="InfoProductBenefit();"> &nbsp;					</td>			</tr>		</table>	</div></body></html>