<?php		require("../sisipan/sessions.php");	require("../fungsi/global.php");	require("../class/MYSQLConnect.php");	require("../class/class.list.table.php");	require("../class/class.application.php");	require("../sisipan/parameters.php");			/** create object **/			$ListPages -> pages = $db -> escPost('v_page'); 		$ListPages -> setPage(20);				$sql = " select a.MISDNId, a.MISDNPrefix, a.MISDNName, 				a.MISDNStatus, b.ProviderCode, 				b.ProviderName, a.MISDNNumber,				concat(a.MISDNPrefix,'',a.MISDNNumber) as PhoneNumber 			from tms_misdn_type a left join tms_misdn_provider b on a.MISDNProvider=b.ProviderId";			//		echo $sql;				$ListPages -> query($sql);					$ListPages -> setWhere();		$ListPages -> setLimit();		$ListPages -> result();		SetNoCache();?><table width="100%" class="custom-grid" cellspacing="0"><thead>	<tr height="20"> 		<th nowrap class="custom-grid th-first">&nbsp;<a href="javascript:void(0);" onclick="doJava.checkedAll('isdn_id');">#</a></th>			<th nowrap class="custom-grid th-middle">&nbsp;No.</th>			<th nowrap class="custom-grid th-middle">&nbsp;MISDN Name </th>		<th nowrap class="custom-grid th-middle">&nbsp;MISDN Prefix</th>		<!--<th nowrap class="custom-grid th-middle">&nbsp;ISDN Number </th> -->               <th nowrap class="custom-grid th-middle">&nbsp;MISDN Provider</th>		<th nowrap class="custom-grid th-lasted">&nbsp;Phone Number </th>			</tr></thead>	<tbody>	<?php		$no = (($ListPages -> start) + 1);		while($row = $db ->fetchrow($ListPages->result)){			$color= ($no%2!=0?'#FAFFF9':'#FFFFFF');	?>			<tr class="onselect" bgcolor="<?php echo $color;?>">				<td class="content-first"><input type="checkbox" value="<?php echo $row -> MISDNId; ?>" name="isdn_id" name="isdn_id"></td>				<td class="content-middle"><?php echo $no; ?></td>				<td class="content-middle"><?php echo $row -> MISDNName; ?></td>				<td class="content-middle" style="color:blue;"><?php echo $row->MISDNPrefix;?></td>				<!--<td class="content-middle"><?php echo $row -> MISDNNumber; ?></td>-->				<td class="content-middle"><?php echo $row ->ProviderCode; ?></td>				<td class="content-lasted"><?php echo $row -> PhoneNumber; ?></td>							</tr>							</tbody>	<?php		$no++;		};	?></table>