show_detail_data',					CustomerId : Customers.CustomerId,					CampaignId : Customers.CampaignId,					CallReasonId :	Customers.CallReasonId,					CustomerName : Customers.CustomerName,					CustomerAddress : Customers.CustomerAddress				}								extendsJQuery.Content()			}			else{				alert("No Customer(s) \nPlease Select Other Campaign Or Other Call Status !");				return false;			}		})	}				/* memanggil Jquery plug in */			$(function()		{			$('#toolbars').extToolbars({				extUrl   :'../gambar/icon',				extTitle :[['Start Call'],['Inventory Data :'],[],['Utilize Data/today :'],[]],				extMenu  :[['gotoCallCustomer'],[],[],[],[]],				extIcon  :[['telephone_go.png'],['grid.png'],[],['table_edit.png'],[]],				extText  :true,				extInput :true,				extOption:[					{						render  : 2,						type	:'label',						label	:"<span style=\"color:#FF4321;\"> "+QueryData.inventory_data+"</span>"					},{						render  : 4,						type	:'label',						label	:"<span style=\"color:#FF4321;\"> "+QueryData.utilize_data+"</span>"					}]			});						$('#cust_dob').datepicker({showOn: 'button', buttonImage: '../gambar/calendar.gif', buttonImageOnly: true, dateFormat:'dd-mm-yy',readonly:true});		});			</script>	<!-- start: css -->	<style>		.select { border:1px solid #bbbbbb;width:160px;font-size:11px;height:22px;background-image:url('../gambar/input_bg.png');}		.input_text {border:1px solid #bbbbbb;width:250px;font-size:12px;height:18px;background-image:url('../gambar/input_bg.png');}		.input_box {border:1px solid #bbbbbb;width:70px;font-size:12px;height:18px;background-image:url('../gambar/input_bg.png');}				.text_header { text-align:right;color:#236777;font-weight:bold;font-size:12px;}		.text_header2 { text-align:right;color:#236777;font-weight:bold;font-size:12px; width:100px}		.select_multiple { border:1px solid #dddddd;width:250px;font-size:11px;background-color:#fffccc;}		.textarea { font-family:Arial;color:blue;height:100px;border:1px solid #dddddd;width:250px;font-size:12px;background-color:#fffccc; }	</style><!-- stop: css -->	<!-- start : content -->			<fieldset class="corner">			<legend class="icon-customers">&nbsp;&nbsp;Customer Follow Up </legend>					<fieldset class="corner" style="margin-bottom:10px;margin-left:5px;">					<legend> Follow Up Criteria </legend>					<div id="span_top_nav">						<table border=0 cellpadding="6px">							<tr>								<td class="text_header"> Campaign Name</td>								<td><?php $jpForm ->jpCombo('CampaignId','select',$db -> Entity -> getAssigmentCampaign($_SESSION['UserId']),$_SESSION['CampaignId'],1);?></td>								<td class="text_header"> Call Status</td>								<td><?php $jpForm ->jpCombo('CallReasonId','select',$db -> Entity -> FollowUp(),$_SESSION['CallReasonId'],1);?></td>							</tr>								<td class="text_header"> Customer Name</td>								<td><?php $jpForm ->jpInput('CustomerName','input_text',NULL);?></td>								<td class="text_header"> Customer Address</td>								<td><?php $jpForm ->jpInput('CustomerAddress','input_text',NULL);?></td>							</tr>						<tr>							<td class="text_header2"> Last Call Date</td>							<td> <script type="text/javascript" src="../pustaka/jquery/jquery-ui-1.7.2/ui/ui.datepicker.js"></script>								<script type="text/javascript">									$(function() {										$("#LastCallDate").datepicker({											showOn : 'button',											buttonImage : '../gambar/calendar.gif',											buttonImageOnly : true,											dateFormat : 'yy-mm-dd'										});									});								</script>							<?php $jpForm ->jpInput('LastCallDate','input_text',NULL);?></td>														<td class="text_header2"> Last Call Hour</td>							<td style="width:10px"><?php $jpForm ->jpInput('LastCallTime','input_text',NULL);?>Please Entry "Hour" between 00 to 24</td>						</tr>														</table>						</div>				</fieldset>					<div id="toolbars"></div>		</fieldset>				<!-- stop : content -->		